void print_array(double* array_pointer, int array_size) {
	
	int counter;
	
	for (counter = 0; counter < array_size; counter++)
		printf("%.4lf\n", *(array_pointer + counter));
	
	printf("\n");

}

void bubble_sort(double* array_pointer, int array_size) {
		
	int counter;

	for (counter = 0; counter < array_size-1; counter++) {
		
		int counter_2;

		for (counter_2 = 0; counter_2 < (array_size - 1 - counter); counter_2++) {
			
			if (*(array_pointer + counter_2) > *(array_pointer + counter_2 + 1)) {
				
				double temp = *(array_pointer + counter_2);
				*(array_pointer + counter_2) = *(array_pointer + counter_2 + 1);
				*(array_pointer + counter_2 + 1) = temp;
				bubble_swaps++;

			}

			bubble_comparisons++;

		}

	}

}

void quick_sort(double* array_pointer, int array_size) {
	
	if (array_size < 2)
		return;

	int left, right;
	double pivot = array_pointer[array_size / 2];

	for (left = 0, right = array_size - 1;; left++, right--) {

		while (array_pointer[left] < pivot) {
			left++;
			quick_comparisons++;
		}
		while (array_pointer[right] > pivot) {
			right--;
			quick_comparisons++;
		}
		if (left >= right)
			break;

		double temp = array_pointer[left];
		array_pointer[left] = array_pointer[right];
		array_pointer[right] = temp;
		quick_swaps++;

	}

	quick_sort(array_pointer, left);
	quick_sort(array_pointer + left, array_size - left);
		
}
